e_pr_num,e_num,p_name, p_content,p_lowerAge, p_upperAge,p_academic,p_requireNum,p_regDate,p_exDate,p_lowerCost,p_upperCost,p_startDate,p_endDate,p_location,p_experience
)
values(
e_request_project_seq.nextval, #{e_num}, #{p_name}, #{p_content}, #{p_lowerAge}, #{p_upperAge},#{p_academic},#{p_requireNum}, SYSDATE, #{p_exDate},#{p_lowerCost},#{p_upperCost},#{p_startDate},#{p_endDate},#{p_location},#{p_experience}	
)

insert into p_job(
e_pr_num, job_id
)
